import React, { useEffect, useState } from 'react'
import { resourceLimits } from 'worker_threads';
import { env } from "../env";


export default function Admin() {
    const [allUser,setAllUser] = useState([{id:"",username:"",password:"",userType:"",permissionsLevel:1}])
    const [index,setIndex] = useState("0")
    const [permissionsLevel, setPermissionsLevel] = useState(0)

    const api_server = env.apiOrigin;

    const allUsersInfoApi = async () => {
        const res = await fetch(`${api_server}/users/getAllUsername`);
        // return res;
        const result = await res.json()
        console.log(result)
        setAllUser(result)
    }

    useEffect(()=>{
        allUsersInfoApi()
        setPermissionsLevel(allUser[parseInt(index)].permissionsLevel)
    },[])

  return (
    <>
        <h2>Admin Portal</h2>
        <button onClick={allUsersInfoApi}>check</button>
        <form action="">
            <select name="" id="" onChange={(e)=>setIndex(e.currentTarget.value)}>
                {allUser.map((user:any,index)=>(
                    <>
                        <option defaultValue={index} value={index} >{user.username}</option>
                    </>
                ))}
            </select>{}
            <label htmlFor=""> Permission Level :</label>
            <input type="text" value={allUser[parseInt(index)].permissionsLevel} onChange={(e)=> e.currentTarget.value}/>
        </form>
    </>
  )
}
