import React, { useEffect, useState } from 'react'
import { resourceLimits } from 'worker_threads';
import { env } from "../env";


export default function Admin() {

    interface user {
        id: number;
        username: string;
      }

    const api_server = env.apiOrigin;
    const [allUser,setAllUser] = useState([])
    const [currentUser,setCurrentUser] = useState()

    const allUsersInfoApi = async () => {
        const res = await fetch(`${api_server}/users/getAllUsername`);
        // return res;
        const result = await res.json()
        console.log(result)
        setAllUser(result)
    }

    useEffect(()=>{
        allUsersInfoApi()
    },[])

  return (
    <>
        <h2>Admin Portal</h2>
        <button onClick={allUsersInfoApi}>check</button>
        <form action="">
            <select name="" id="">
                {allUser.map((user:any)=>(
                    <>
                        <option value={user.username} onChange={(e)=>setCurrentUser(e.currentTarget.value)}>{user.username}</option>
                    </>
                ))}
            </select>
            <input type="text" value={currentUser.permissionsLevel} />
        </form>
    </>
  )
}
