import { unwatchFile } from 'fs';
import { env } from "../env";
import React, { useState,useEffect } from 'react'

export default function Policy() {
    const [policyNumber, setPolicyNumber] = useState("");
    const [policyType, setPolicyType] = useState('');
    const [policyTerm, setPolicyTerm] = useState('');
    const [coverageAmount, setCoverageAmount] = useState('');
    const [premium, setPremium] = useState('');
    const [allPolicy,setAllPolicy] = useState([{id:'',policy_number:'',policy_type:'',policy_term:'',coverage_amount:'',premium:''}])
    const [policyId,setPolicyId] = useState(null)
    const [editMode,setEditMode] = useState(false) 

    const api_server = env.apiOrigin;

    useEffect(()=>{
        getAllPolicyApi()
    },[])

    const submitPolicyApi = async () => {
        const res = await fetch(`${api_server}/users/postPolicy`, {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              // Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({
                policyNumber,
                policyType,
                policyTerm,
                coverageAmount,
                premium,
            }),

          });
        if(res.status === 200){
            getAllPolicyApi()
        }
    }

    const getAllPolicyApi = async () => {
        const res = await fetch(`${api_server}/users/getAllPolicy`)
        const result = await res.json()
        setAllPolicy(result)
    }

    const editPostApi = async (policyId:string) => {
        const res = await fetch(`${api_server}/users/editPolicy`,{
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({
            policyId,
            policyNumber,
            policyType,
            policyTerm,
            coverageAmount,
            premium,
          }),
        });
        if(res.status === 200){
            getAllPolicyApi()
        }
      }

    const deletePostApi = async (policyId:string) => {
        const res = await fetch(`${api_server}/users/deletePolicy`,{
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            // Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
          body: JSON.stringify({
            policyId: policyId,
          }),
        });
        if(res.status === 200){
            getAllPolicyApi()
        }
      }

    const handleSubmitPolicy=(e:any)=>{
        e.preventDefault()
        submitPolicyApi()
    }

    const editPolicy = (policyId:string) => {
        editPostApi(policyId)
        setEditMode(!editMode)
    }

    const deletePolicy = (policyId:string) =>{
        deletePostApi(policyId)
    }

  return (
    <>
        <h2>Policy Manangement System</h2>
        <form onSubmit={handleSubmitPolicy} style={{display:'flex',flexDirection:'column',alignItems:'center'}}>
            <label> Policy number: </label>
            <input type="text" value={policyNumber} onChange={(e)=>setPolicyNumber(e.currentTarget.value)}/>
            <label> Policy type: </label>
            <input type="text" value={policyType} onChange={(e)=>{setPolicyType(e.currentTarget.value)}}/>
            <label> PolicyTerm: </label>
            <input type="text" value={policyTerm} onChange={(e)=>{setPolicyTerm(e.currentTarget.value)}}/>
            <label> Coverage amount: </label>
            <input type="text" value={coverageAmount} onChange={(e)=>{setCoverageAmount(e.currentTarget.value)}}/>
            <label> Premium: </label>
            <input type="text" value={premium} onChange={(e)=>{setPremium(e.currentTarget.value)}}/>
            <input type="submit" />
        </form>
        <h3>Policy List</h3>
        <div style={{display:'flex',flexFlow:'wrap'}}>
        {allPolicy.map((policy)=>(
            <div>
                {!editMode?
                    <div style={{border:'solid 2px',marginLeft:'5px',marginRight:'2px',marginBottom:'5px',padding:'10px'}}>
                        <div style={{textAlign:"left",marginBottom:'5px'}}>
                            <div>Policy number:{policy.policy_number}</div>
                            <div>Policy type: {policy.policy_type}</div>
                            <div>PolicyTerm: {policy.policy_term}</div>
                            <div>Coverage amount: {policy.coverage_amount}</div>
                            <div>Premium: {policy.premium}</div>
                        </div>
                        <div>
                        <button onClick={() => editPolicy(policy.id)}>Edit</button>
                        <button onClick={() => deletePolicy(policy.id)}>Delete</button>
                        </div>
                    </div>
                    :
                    <div style={{border:'solid 2px',marginLeft:'2px',marginRight:'2px',marginBottom:'5px',padding:'10px'}}>
                        <label htmlFor="">Policy number:</label>
                        <input type="text" onChange={(e)=>setPolicyNumber(e.currentTarget.value)} value={policyNumber}/>
                        <label htmlFor="">Policy type:</label>
                        <input type="text" onChange={(e)=>setPolicyType(e.currentTarget.value)} value={policyType}/>
                        <label htmlFor="">PolicyTerm:</label>
                        <input type="text" onChange={(e)=>setPolicyTerm(e.currentTarget.value)} value={policyTerm}/>
                        <label htmlFor="">Coverage amount:</label>
                        <input type="text" onChange={(e)=>setCoverageAmount(e.currentTarget.value)} value={coverageAmount}/>
                        <label htmlFor="" >Premium:</label>
                        <input type="text" onChange={(e)=>setPremium(e.currentTarget.value)} value={premium}/>
                        <div>
                        <button onClick={() => editPolicy(policy.id)}>Save</button>
                        <button onClick={() => deletePolicy(policy.id)}>Delete</button>
                        </div>
                    </div> 
                }
            </div>
        ))}
        </div>
    </>
  )
}
