import React, { useEffect, useState } from 'react'
import { resourceLimits } from 'worker_threads';
import { env } from "../env";


export default function Admin() {

    interface allUserState {
        id: number;
        username: string;
      }

    const api_server = env.apiOrigin;
    const [allUser,setAllUser] = useState([{id:"",username:"",password:"",userType:"",permissionsLevel:""}])
    const [currentName, setCurrentName] = useState("")
    const [currentUser,setCurrentUser] = useState("")
    const [index,setIndex] = useState("1")
    const [random,setRandom] = useState(1)

    const allUsersInfoApi = async () => {
        const res = await fetch(`${api_server}/users/getAllUsername`);
        // return res;
        const result = await res.json()
        console.log(result)
        setAllUser(result)
        console.log('currentUser',currentUser)
    }

    useEffect(()=>{
        allUsersInfoApi()
        setRandom(Math.random() * 1000)
    },[])

    // useEffect(()=>{
    //     console.log('currentName',currentName)
    // },[currentName])

    useEffect(()=>{
        console.log('index',index)
        console.log('alluser',allUser[parseInt(index)])
        console.log(random)
    },[random])

  return (
    <>
        <h2>Admin Portal</h2>
        <button onClick={allUsersInfoApi}>check</button>
        <form action="">
            <select name="" id="" onChange={(e)=>setIndex(e.currentTarget.value)}>
                {allUser.map((user:any,index)=>(
                    <>
                        <option selected value={index} >{user.username}</option>
                    </>
                ))}
            </select>{}
            <input type="text" value={allUser[parseInt(index)].permissionsLevel}/>
        </form>
    </>
  )
}
