import React, { useEffect, useState } from 'react'
import { resourceLimits } from 'worker_threads';
import { env } from "../env";


export default function Admin() {

    interface user {
        id: number;
        username: string;
      }

    const api_server = env.apiOrigin;
    const [allUser,setAllUser] = useState([])
    const [currentName, setCurrentName] = useState("")
    const [currentUser,setCurrentUser] = useState("")

    const allUsersInfoApi = async () => {
        const res = await fetch(`${api_server}/users/getAllUsername`);
        // return res;
        const result = await res.json()
        console.log(result)
        setAllUser(result)
        console.log('currentUser',currentUser)
    }

    useEffect(()=>{
        allUsersInfoApi()
    },[])

    useEffect(()=>{
        console.log('currentName',currentName)
    },[currentName])

  return (
    <>
        <h2>Admin Portal</h2>
        <button onClick={allUsersInfoApi}>check</button>
        <form action="">
            <select name="" id="" onChange={(e)=>setCurrentName(e.currentTarget.value)}>
                {allUser.map((user:any,index)=>(
                    <>
                        <option value={user.username} >{user.username}</option>
                        {/* <input type="text" value={allUser[index]}/> */}
                        <span>{allUser[index]}</span>
                    </>
                ))}
            </select>
            <input type="text" value={currentUser} />
        </form>
    </>
  )
}
